import axios from 'axios'
import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Form, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import FormContainer from '../components/FormContainer'
import { createFeedback } from '../actions/feedbackActions'
import {FEEDBACK_CREATE_RESET } from '../constants/feedbackConstants'
import { FaWhatsappSquare, FaPhoneSquareAlt } from 'react-icons/fa'
const FeedbackScreen = ({ match, history }) => {
  const feedbackId = match.params.id

  const [bookName, setBookName] = useState('')
  const [authorName, setAuthorName] = useState('')
  const [image, setImage] = useState('')
  const [feedback, setFeedback] = useState('')
  const [uploading, setUploading] = useState(false)
 const [email, setEmail] = useState('')
 

  const dispatch = useDispatch()



  const feedbackCreate = useSelector((state) => state.feedbackCreate)
  const {
    loading: loadingCreate,
    error: errorCreate,
    success: successCreate,
  } = feedbackCreate

  useEffect(() => {
  
    if (successCreate) {
      dispatch({ type: FEEDBACK_CREATE_RESET })
      history.push('/')
    } 
  }, [dispatch, history, successCreate])

  const uploadFileHandler = async (e) => {
    const file = e.target.files[0]
    const formData = new FormData()
    formData.append('image', file)
    setUploading(true)

    try {
      const config = {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      }

      const { data } = await axios.post('/api/upload', formData, config)

      setImage(data)
      setUploading(false)
    } catch (error) {
      console.error(error)
      setUploading(false)
    }
  }

  const submitHandler = (e) => {
    e.preventDefault()
  
    dispatch(
     createFeedback({
        _id: feedbackId,
        bookName,
        authorName,
        image,
        feedback,

      })
    )
  }
  const submitHandlerFeedback = (e) => {
    e.preventDefault()
  
    dispatch(
     createFeedback({
        _id: feedbackId,
        feedback,
        bookName,
      })
    )
  }

  return (
    <div className='feedback'>
      <Link to='/' className='btn btn-light my-3' style={{ backgroundImage: 'linear-gradient(to bottom right,#0a0366,#475cd1,#8ec7f5)',color:'white',fontWeight:'600'}}>
        Go Back
      </Link>
      <Form onSubmit={submitHandlerFeedback}>
        
      <FormContainer>
        <h1 className='tag' style={{fontSize:'45px',fontFamily:'Lucida Console',fontWeight:'bold'}}>Feedback</h1>
        {loadingCreate && <Loader />}
        {errorCreate && <Message variant='danger'>{errorCreate}</Message>}
        <h4 style={{color:'#5c7699',textAlign:'center',fontWeight:'bold'}}>We all need people who will give us feedback. That’s how we improve.</h4><br/><br/><br/><br/>
        <Form.Group controlId='feedback'>
              <Form.Label style={{fontSize:'18px',fontWeight:'bold',color:'black'}}>Feedback</Form.Label>
              <Form.Control
               as='textarea'
               row='3'
                placeholder='feedback'
                value={feedback}
                onChange={(e) => setFeedback(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <br/>
            <Button type='submit'
              style={{ backgroundImage: 'linear-gradient(to bottom right,#50025c, #d20be0,#db3bb6)',color:'white',fontWeight:'600',float:'right',width:'120px'}}>
             Send
            </Button>
            </FormContainer>
            </Form>
            <br/><hr/><br/>

        
          <Form onSubmit={submitHandler}>
          <FormContainer>
        <h1>Request Books</h1>
        {loadingCreate && <Loader />}
        {errorCreate && <Message variant='danger'>{errorCreate}</Message>}
            <Form.Group controlId='bookname'>
              <Form.Label>Book Title</Form.Label>
              <Form.Control
                type='name'
                placeholder='Enter title of book'
                value={bookName}
                onChange={(e) => setBookName(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='authorname'>
              <Form.Label>Author</Form.Label>
              <Form.Control
                type='name'
                placeholder='Enterauthor name'
                value={authorName}
                onChange={(e) =>  setAuthorName(e.target.value)}
              ></Form.Control>
            </Form.Group>


            <Form.Group controlId='image'>
              <Form.Label>Image</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter image url'
                value={image}
                onChange={(e) => setImage(e.target.value)}
              ></Form.Control>
              <Form.File
                id='image-file'
                label='Choose File'
                custom
                onChange={uploadFileHandler}
              ></Form.File>
              {uploading && <Loader />}
            </Form.Group>

           
<br/>
            <Button type='submit'
              style={{ backgroundImage: 'linear-gradient(to bottom right,#50025c, #d20be0,#db3bb6)',color:'white',fontWeight:'600',float:'right',width:'120px'}}>
             Request
            </Button>
            </FormContainer>
          </Form>
        
     
      <div className='contact'>
          <a href='tel:+94776408775' target='_blank'>
            <FaPhoneSquareAlt size={'60px'} color={'#4CCC5B'} />
          </a>
          <a href='https://wa.me/+94770334171' target='_blank'>
            <FaWhatsappSquare size={'60px'} color={'#4CCC5B'} />
          </a>
        </div>
    </div>
    
  )
}

export default FeedbackScreen
