import axios from 'axios'
import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Form, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import FormContainer from '../components/FormContainer'
import { createFeedback } from '../actions/feedbackActions'
import {FEEDBACK_CREATE_RESET } from '../constants/feedbackConstants'

const FeedbackScreen = ({ match, history }) => {
  const feedbackId = match.params.id

  const [bookname, setBookName] = useState('')
  const [authorname, setAuthorName] = useState('')
  const [image, setImage] = useState('')
  const [feedback, setFeedback] = useState('')
  const [uploading, setUploading] = useState(false)

  const dispatch = useDispatch()



  const feedbackCreate = useSelector((state) => state.feedbackCreate)
  const {
    loading: loadingCreate,
    error: errorCreate,
    success: successCreate,
  } = feedbackCreate

  useEffect(() => {
    if (successCreate) {
      dispatch({ type: FEEDBACK_CREATE_RESET })
      history.push('/')
    } 
  }, [dispatch, history, successCreate])

  const uploadFileHandler = async (e) => {
    const file = e.target.files[0]
    const formData = new FormData()
    formData.append('image', file)
    setUploading(true)

    try {
      const config = {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      }

      const { data } = await axios.post('/api/upload', formData, config)

      setImage(data)
      setUploading(false)
    } catch (error) {
      console.error(error)
      setUploading(false)
    }
  }

  const submitHandler = (e) => {
    e.preventDefault()
    dispatch(
     createFeedback({
        _id: feedbackId,
        bookname,
        authorname,
        image,
        feedback,
      })
    )
  }

  return (
    <>
      <Link to='/' className='btn btn-light my-3'>
        Go Back
      </Link>
      <FormContainer>
        <h1>Feedback</h1>
        {loadingCreate && <Loader />}
        {errorCreate && <Message variant='danger'>{errorCreate}</Message>}
        
          <Form onSubmit={submitHandler}>
            <Form.Group controlId='bookname'>
              <Form.Label>Book Title</Form.Label>
              <Form.Control
                type='name'
                placeholder='Enter title of book'
                value={bookname}
                onChange={(e) => setBookName(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='authorname'>
              <Form.Label>Author</Form.Label>
              <Form.Control
                type='name'
                placeholder='Enterauthor name'
                value={authorname}
                onChange={(e) =>  setAuthorName(e.target.value)}
              ></Form.Control>
            </Form.Group>


            <Form.Group controlId='image'>
              <Form.Label>Image</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter image url'
                value={image}
                onChange={(e) => setImage(e.target.value)}
              ></Form.Control>
              <Form.File
                id='image-file'
                label='Choose File'
                custom
                onChange={uploadFileHandler}
              ></Form.File>
              {uploading && <Loader />}
            </Form.Group>

            <Form.Group controlId='feedback'>
              <Form.Label>Feedback</Form.Label>
              <Form.Control
                type='text'
                placeholder='feedback'
                value={feedback}
                onChange={(e) => setFeedback(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Button type='submit' variant='primary'>
             Send
            </Button>
          </Form>
        
      </FormContainer>
    </>
  )
}

export default FeedbackScreen
