import React, { useEffect,useState } from 'react'
import { LinkContainer } from 'react-router-bootstrap'
import { Table, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { listFeedbacks } from '../actions/feedbackActions'

const FeedbackListScreen = ({ history }) => {
  const dispatch = useDispatch()
  const [num, setNum] = useState(0)
  const feedbackList = useSelector((state) => state.feedbackList)
  const { loading, error, feedbacks } = feedbackList

  const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin

  useEffect(() => {
    let i=setNum(num+1);

    if (userInfo &&  userInfo.isSystemAdmin) {
      dispatch(listFeedbacks())
    }
    else if (userInfo &&  userInfo.isAdmin) {
      dispatch(listFeedbacks())
    }
    else {
      history.push('/login')
    }
  }, [dispatch, history, userInfo])

  return (
    <>
      <h1>Feedbacks</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <Table striped bordered hover responsive className='table-sm'>
          <thead>
            <tr>
              <th>No</th>
              <th>USER</th>
              <th>Email</th>
              <th>Feedback</th>
              <th>Requested Book</th>
              
              <th></th>
            </tr>
          </thead>
          <tbody>
            {feedbacks.map((feedback,i) => (
              <tr key={feedback._id}>
                <td>{i+1}</td>
                <td>{feedback.user && feedback.user.name}</td>
                <td>{feedback.user && feedback.user.email}</td>
                <td>{feedback.feedback !='' ? (
                    <i className='fas fa-check' style={{ color: 'green' }}></i>
                  ) : (
                    <i className='fas fa-times' style={{ color: 'red' }}></i>
                  )}</td>
                <td>
                {feedback.bookName !='' ? (
                    <i className='fas fa-check' style={{ color: 'green' }}></i>
                  ) : (
                    <i className='fas fa-times' style={{ color: 'red' }}></i>
                  )}
                </td>
               
                <td>
                  <LinkContainer to={`/admin/feedback/${feedback._id}`}>
                    <Button variant='light' className='btn-sm'>
                      Details
                    </Button>
                  </LinkContainer>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </>
  )
}

export default  FeedbackListScreen
