import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, Image, ListGroup, Card, Button, Form } from 'react-bootstrap'
import { showOrder } from '../actions/orderActions'
import Message from '../components/Message'
import Loader from '../components/Loader'
import Meta from '../components/Meta'
import {
  listProductDetails,
  createProductReview,
} from '../actions/productActions'
import { PRODUCT_CREATE_REVIEW_RESET } from '../constants/productConstants'

const ConfirmScreen = ({ history, match }) => {
   // history.push(`/order/${order._id}`)

    const orderDetails = useSelector((state) => state.orderDetails)
    const { order} = orderDetails
    
    const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin

    const  orderShow = useSelector((state) => state. orderShow)
    const { loading: loadingShow, success: successShow } =  orderShow
  const dispatch = useDispatch()




  const addToTimer = () => {
    history.push('/')
    setTimeout(() => {
      dispatch(showOrder(match.params.id))
    }, 1000);
   
  }



  return (
    <>
     
     <h2>You have 60 min to cancel this order</h2>

     <Button
         onClick={addToTimer}
         className='btn-block'
            type='button'
           
        >
           Ok
    </Button>
    </> )
}

export default ConfirmScreen
