import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, Image, ListGroup, Card, Button, Form } from 'react-bootstrap'
import Rating from '../components/Rating'
import Message from '../components/Message'
import Loader from '../components/Loader'
import Meta from '../components/Meta'
import {
    getFeedbackDetails
} from '../actions/feedbackActions'


const FeedbackDetailScreen = ({ history, match }) => {
  

  const dispatch = useDispatch()

  const feedbackDetails = useSelector((state) => state.feedbackDetails)
  const { loading, error, feedback } = feedbackDetails

  const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin


  useEffect(() => {
   
    if (userInfo &&  userInfo.isSystemAdmin) {
        dispatch(getFeedbackDetails(match.params.id))
      }
      else if (userInfo &&  userInfo.isAdmin) {
        dispatch(getFeedbackDetails(match.params.id))
      }
      else {
        history.push('/login')
   
      }
      
    
  }, [dispatch,history, userInfo,feedback, match])
 


  return (
    <>
      <Link className='btn btn-outline-primary btn-sm my-3' to='/'>
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <>
         
          <Row>
            <Col md={6}>
              <Image className='bookImg ' src={feedback.image} alt={feedback.bookName} fluid />
            </Col>
            <Col md={6}>
              <ListGroup variant='flush'>
                <ListGroup.Item>
                  <h3>{feedback.bookName}</h3>
                </ListGroup.Item>
                <ListGroup.Item>
                  <h3>{feedback.authorName}</h3>
                </ListGroup.Item>
              </ListGroup>
            </Col>
            
          </Row>
        </>
      )}
    </>
  )
}

export default FeedbackDetailScreen
