import React, { useState, useEffect } from 'react'
import {
  Button,
  Row,
  Col,
  Form,
  Container,
  Alert,
  Spinner,
} from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { register } from '../actions/userActions'
import { useFormik } from 'formik'
import * as yup from 'yup'
import YupPassword from 'yup-password'
YupPassword(yup)

const Loader = () => {
  return (
    <Spinner
      animation='border'
      role='status'
      style={{
        width: '100px',
        height: '100px',
        margin: 'auto',
        display: 'block',
      }}
    ></Spinner>
  )
}

const Message = ({ variant, children }) => {
  return <Alert variant={variant}>{children}</Alert>
}

Message.defaultProps = {
  variant: 'info',
}




const FormContainer = ({ children }) => {
  return (
    <Container>
      <Row className='justfy-content-md-center'>
        <div xs={12} md={6}>
          {children}
        </div>
      </Row>
    </Container>
  )
}

const RegisterScreen = ({ location, history }) => {
  const dispatch = useDispatch()

  const formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      password: '',
      confirmPassword: '',
    },
    validationSchema: yup.object({
      name: yup.string().max(20, 'Maximum 20 characters only').required(),

      email: yup.string().email().required(),

      password: yup.string().required('Password is required').password(),

      confirmPassword: yup
        .string()
        .required('Confirm Password is required')
        .oneOf(
          [yup.ref('password'), null],
          'Confirm password and password must be same'
        ),
    }),
    onSubmit: (userInputData) => {
      dispatch(register(userInputData.name, userInputData.email, userInputData.password))
    },
  })


  const [message] = useState(null)

  const userRegister = useSelector((state) => state.userRegister)
  const { loading, error, userInfo } = userRegister

  const redirect = window.location.search
    ? window.location.search.split('=')[1]
    : '/'

    useEffect(() => {
      if (userInfo) {
        history.push(redirect)
      }
    }, [history, userInfo, redirect])

  return (
    <FormContainer>
      <div className='form'>
        <h1>Sign Up</h1>
        {message && <Message variant='danger'>{message}</Message>}
        {error && <Message variant='danger'>{error}</Message>}
        {loading && <Loader />}

        <Form onSubmit={formik.handleSubmit}>
          <Form.Group controlId='name'>
            <Form.Label>Name</Form.Label>
            <Form.Control
              type='name'
              name='name'
              placeholder='Enter name'
              value={formik.values.name}
              onChange={formik.handleChange}
            ></Form.Control>
            {formik.errors.name ? (
              <div className='text-danger'>{formik.errors.name}</div>
            ) : null}
          </Form.Group>

          <Form.Group controlId='email'>
            <Form.Label>Email Address</Form.Label>
            <Form.Control
              type='email'
              placeholder='Enter email'
              name='email'
              value={formik.values.email}
              onChange={formik.handleChange}
            ></Form.Control>
            {formik.errors.email ? (
              <div className='text-danger'>{formik.errors.email}</div>
            ) : null}
          </Form.Group>

          <Form.Group controlId='password'>
            <Form.Label>Password</Form.Label>
            <Form.Control
              type='password'
              placeholder='Enter password'
              name='password'
              value={formik.values.password}
              onChange={formik.handleChange}
            ></Form.Control>
            {formik.errors.password ? (
              <div className='text-danger'>{formik.errors.password}</div>
            ) : null}
          </Form.Group>

          <Form.Group controlId='confirmPassword'>
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control
              type='password'
              placeholder='Confirm password'
              name='confirmPassword'
              value={formik.values.confirmPassword}
              onChange={formik.handleChange}
            ></Form.Control>
            {formik.errors.confirmPassword ? (
              <div className='text-danger'>{formik.errors.confirmPassword}</div>
            ) : null}
          </Form.Group>

          <Button style={{ marginTop: 5 }} type='submit' variant='primary'>
            Register
          </Button>
        </Form>

        <Row className='py-3'>
          <Col>
            Have an Account?{' '}
            <Link to={redirect ? `/login?redirect=${redirect}` : '/login'}>
              Login
            </Link>
          </Col>
        </Row>
      </div>
    </FormContainer>
  )
}

export default RegisterScreen
